import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductService } from '../productservice.service';
import Pro

@Component({
  selector: 'app-productlist',
  templateUrl: './productlist.component.html',
  styleUrls: ['./productlist.component.css']
})
export class ProductListComponent implements OnInit {
  category: string = '';
  products: Product[] = [];

  constructor(
    private route: ActivatedRoute,
    private productService: ProductService
  ) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      this.category = params.get('category') || 'mobiles'; // Default to mobiles
      this.fetchProducts();
    });
  }

  fetchProducts(): void {
    this.productService.getProductsByCategory(this.category).subscribe(data => {
      this.products = data;
    });
  }
}
<div class="container">
    <h2>{{ category | titlecase }} List</h2>

    <div class="product-grid">
        <div class="product-card" *ngFor="let product of products">
            <img [src]="product.image" [alt]="product.name" />
            <h3>{{ product.name }}</h3>
            <p><strong>Price:</strong> {{ product.price }}</p>
            <p><strong>Features:</strong> {{ product.features }}</p>
        </div>
    </div>
</div>
