import { Component } from '@angular/core';

@Component({
  selector: 'app-homepage',
  templateUrl: './homepage.component.html',
  styleUrls: ['./homepage.component.css']
})
export class HomepageComponent {
  cartItemCount = 0; // Default cart count

  constructor() {}

  addToCart() {
    this.cartItemCount++; // Simulates adding items to the cart
  }
}
<!-- Navigation Bar -->
<nav class="navbar">
    <h2 class="logo">ElectroMart</h2>

    <div class="nav-buttons">
        <button class="btn-theme" onclick="changeTheme('raspberry')">Raspberry & Green</button>
        <button class="btn-theme" onclick="changeTheme('teal')">Teal & Pink</button>
        <button class="btn-theme" onclick="changeTheme('blue-gray')">Blue-Gray & Orange</button>
        <button class="btn-theme" onclick="changeTheme('olive')">Olive & Light Orange</button>

        <button class="btn-login">Login</button>

        <div class="cart-container">
            <img src="assets/cart.png" class="cart-icon" alt="Cart">
            <span class="cart-count">3</span>
        </div>
    </div>
</nav>

<!-- Background Container -->
<div class="background-container" id="theme-background">
    <div class="content">

        <!-- üîç Search Box -->
        <div class="search-box">
            <input type="text" class="search-input" placeholder="Search for products, brands & more...">
            <button class="search-button">üîç</button>
        </div>

        <!-- Product Categories -->
        <div class="products-section">
            <h2>Explore Our Categories</h2>
            <div class="product-cards">
                <div class="product-card">
                    <a routerLink="/mobiles">
                        <img src="assets/mobiles.jpg" alt="Mobile Phones">
                        <h3>Mobile Phones</h3>
                    </a>
                </div>

                <div class="product-card">
                    <a routerLink="/laptops">
                        <img src="assets/laptops.jpg" alt="Laptops">
                        <h3>Laptops</h3>
                    </a>
                </div>

                <div class="product-card">
                    <a routerLink="/headphones">
                        <img src="assets/headphones.jpg" alt="Headphones">
                        <h3>Headphones</h3>
                    </a>
                </div>
            </div>
        </div>
    </div>
</div>

<script>
    function changeTheme(theme) {
        let background = document.getElementById('theme-background');
        let searchBox = document.querySelector('.search-box');
        let buttons = document.querySelectorAll('.btn-theme');

        if (theme === 'raspberry') {
            background.style.background = "linear-gradient(135deg, #8B0000, #4CAF50)";
            searchBox.style.background = "#4CAF50";
        } else if (theme === 'teal') {
            background.style.background = "linear-gradient(135deg, #008080, #FF1493)";
            searchBox.style.background = "#FF1493";
        } else if (theme === 'blue-gray') {
            background.style.background = "linear-gradient(135deg, #607D8B, #FFA500)";
            searchBox.style.background = "#FFA500";
        } else if (theme === 'olive') {
            background.style.background = "linear-gradient(135deg, #556B2F, #FFB347)";
            searchBox.style.background = "#FFB347";
        }

        buttons.forEach(btn => {
            btn.style.opacity = theme === btn.textContent.toLowerCase().replace('& ', '').replace(' ', '-') ? '1' : '0.6';
        });
    }
</script>
