import { Component, OnInit } from '@angular/core';
import { Product } from '../models/product.model'; // Import Product Model
import { ProductService } from '../productservice.service';

@Component({
  selector: 'app-mobiles',
  templateUrl: './mobiles.component.html',
  styleUrls: ['./mobiles.component.css']
})
export class MobilesComponent implements OnInit {
  isMobileListVisible = false;
  mobiles: Product[] = []; // Use Product model

  constructor(private productService: ProductService) {}

  ngOnInit() {
    this.fetchMobiles();
  }

  fetchMobiles() {
    this.productService.getMobiles().subscribe(data => {
      this.mobiles = data;
    });
  }

  showMobiles() {
    this.isMobileListVisible = !this.isMobileListVisible;
  }
}
<div class="container">
    <!-- Button to Show/Hide Mobile List -->
    <button class="toggle-button" (click)="showMobiles()">
        {{ isMobileListVisible ? 'Hide Mobiles' : 'Show Mobiles' }}
    </button>

    <!-- Mobile List Grid (Visible Only if List is Enabled) -->
    <div *ngIf="isMobileListVisible" class="mobile-grid">
        <div class="mobile-card" *ngFor="let mobile of mobiles">
            <img [src]="mobile.image" [alt]="mobile.name" />
            <h3>{{ mobile.name }}</h3>
            <p><strong>Price:</strong> {{ mobile.price }}</p>
            <p><strong>Features:</strong> {{ mobile.features }}</p>
        </div>
    </div>
</div>
