import { Component } from '@angular/core';

@Component({
  selector: 'app-homepage',
  templateUrl: './homepage.component.html',
  styleUrls: ['./homepage.component.css']
})
export class HomepageComponent {

}
<html>
<head>
    <title>Home Page</title>
    <link rel="stylesheet" 
        href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css">
    <style>
        /* Full-page centering with gradient background */
        .center-container {
            min-height: 100vh; /* Full height */
            display: flex;
            flex-direction: column;
            align-items: center;
            text-align: center;
            background: linear-gradient(135deg, #ff9a9e, #fad0c4); /* Attractive gradient */
            color: white; /* White text */
            padding-bottom: 50px;
        }

        /* Styling the search input */
        .search-input {
            border: 2px solid white;
            border-radius: 25px; /* Rounded corners */
            padding: 12px;
            font-size: 18px;
            background-color: rgba(255, 255, 255, 0.8); /* Transparent white */
        }

        .search-input::placeholder {
            color: #555; /* Darker placeholder text */
            font-style: italic;
        }

        /* Product styling */
        .product-card {
            border-radius: 10px;
            overflow: hidden;
            transition: transform 0.3s ease-in-out;
        }

        .product-card:hover {
            transform: scale(1.05);
        }
    </style>
</head>
<body>

    <div class="container center-container">
        <h1>Welcome to Our Store</h1>

        <!-- Search Bar -->
        <div class="row justify-content-center w-100 mb-4">
            <div class="col-md-6 col-sm-8 col-10">
                <input type="text" class="form-control text-center search-input" placeholder="Search for products...">
            </div>
        </div>

        <!-- Product Grid -->
        <div class="container">
            <div class="row">
                <!-- Product 1 -->
                <div class="col-md-4 col-sm-6 mb-4">
                    <div class="card product-card">
                        <img src="https://via.placeholder.com/300" class="card-img-top" alt="Product 1">
                        <div class="card-body text-dark">
                            <h5 class="card-title">Product 1</h5>
                            <p class="card-text">$29.99</p>
                            <button class="btn btn-primary">Add to Cart</button>
                        </div>
                    </div>
                </div>

                <!-- Product 2 -->
                <div class="col-md-4 col-sm-6 mb-4">
                    <div class="card product-card">
                        <img src="https://via.placeholder.com/300" class="card-img-top" alt="Product 2">
                        <div class="card-body text-dark">
                            <h5 class="card-title">Product 2</h5>
                            <p class="card-text">$39.99</p>
                            <button class="btn btn-primary">Add to Cart</button>
                        </div>
                    </div>
                </div>

                <!-- Product 3 -->
                <div class="col-md-4 col-sm-6 mb-4">
                    <div class="card product-card">
                        <img src="https://via.placeholder.com/300" class="card-img-top" alt="Product 3">
                        <div class="card-body text-dark">
                            <h5 class="card-title">Product 3</h5>
                            <p class="card-text">$49.99</p>
                            <button class="btn btn-primary">Add to Cart</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    </div>

</body>
</html>
