import { Component } from '@angular/core';

@Component({
  selector: 'app-homepage',
  templateUrl: './homepage.component.html',
  styleUrls: ['./homepage.component.css']
})
export class HomepageComponent {
  cartItemCount = 0; // Default cart count

  constructor() {}

  addToCart() {
    this.cartItemCount++; // Simulates adding items to the cart
  }

  currentBackground: string = 'default-bg';

  changeBackground(category: string) {
    if (category === 'mobiles') {
      this.currentBackground = 'mobiles-bg';
    } else if (category === 'laptops') {
      this.currentBackground = 'laptops-bg';
    } else if (category === 'headphones') {
      this.currentBackground = 'headphones-bg';
    }
  }

  resetBackground() {
    this.currentBackground = 'default-bg';
  }
}
<!-- Navigation Bar -->
<nav class="navbar">
    <h2 class="logo">ElectroMart</h2>

    <div class="nav-right">
        <!-- 🟢 Added routerLink for navigation -->
        <button class="btn-login" routerLink="/login">Login</button>

        <div class="cart-container">
            <a routerLink="/cart">
                <img src="assets/cart.png" class="cart-icon" alt="Cart">
                <span class="cart-count">3</span>
            </a>
        </div>
    </div>
</nav>

<!-- Background Container with Dynamic Class -->
<div class="background-container" [ngClass]="currentBackground">
    <div class="content">

        <!-- 🔍 Search Box -->
        <div class="search-box">
            <input type="text" class="search-input" placeholder="Search for products, brands & more...">
            <button class="search-button">🔍</button>
        </div>

        <!-- Product Categories -->
        <div class="products-section">
            <h2>Explore Our Categories</h2>
            <div class="product-cards">
                <div class="product-card" 
                    (mouseenter)="changeBackground('mobiles')" 
                    (mouseleave)="resetBackground()">
                    <a routerLink="/mobiles">
                        <img src="assets/mobiles.jpg" alt="Mobile Phones">
                        <h3>Mobile Phones</h3>
                    </a>
                </div>

                <div class="product-card" 
                    (mouseenter)="changeBackground('laptops')" 
                    (mouseleave)="resetBackground()">
                    <a routerLink="/laptops">
                        <img src="assets/laptops.jpg" alt="Laptops">
                        <h3>Laptops</h3>
                    </a>
                </div>

                <div class="product-card" 
                    (mouseenter)="changeBackground('headphones')" 
                    (mouseleave)="resetBackground()">
                    <a routerLink="/headphones">
                        <img src="assets/headphones.jpg" alt="Headphones">
                        <h3>Headphones</h3>
                    </a>
                </div>
            </div>
        </div>
    </div>
</div>
