import { Component } from '@angular/core';

@Component({
  selector: 'app-homepage',
  templateUrl: './homepage.component.html',
  styleUrls: ['./homepage.component.css']
})
export class HomepageComponent {

}
<html>
<head>
    <title>Electronics Store</title>
    <link rel="stylesheet" 
        href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css">
    <style>
        /* Full-page centering with gradient background */
        .center-container {
            min-height: 100vh; /* Full height */
            display: flex;
            flex-direction: column;
            align-items: center;
            text-align: center;
            background: linear-gradient(135deg, #ff9a9e, #fad0c4); /* Attractive gradient */
            color: white; /* White text */
            padding-bottom: 50px;
        }

        /* Styling the search input */
        .search-input {
            border: 2px solid white;
            border-radius: 25px; /* Rounded corners */
            padding: 12px;
            font-size: 18px;
            background-color: rgba(255, 255, 255, 0.8); /* Transparent white */
        }

        .search-input::placeholder {
            color: #555; /* Darker placeholder text */
            font-style: italic;
        }

        /* Category Styling */
        .category-card {
            text-decoration: none; /* Remove link underline */
            color: black;
            text-align: center;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
        }

        .category-img-container {
            width: 150px;
            height: 150px;
            border-radius: 50%;
            overflow: hidden;
            display: flex;
            align-items: center;
            justify-content: center;
            background: rgba(255, 255, 255, 0.8); /* White background for contrast */
            box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2); /* Soft shadow effect */
            transition: transform 0.3s ease-in-out;
        }

        .category-img-container:hover {
            transform: scale(1.1);
        }

        .category-img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            border-radius: 50%; /* Circular image */
        }

        .category-title {
            font-size: 18px;
            font-weight: bold;
            margin-top: 10px;
            color: white;
        }
    </style>
</head>
<body>

    <div class="container center-container">
        <h1>Welcome to Electronics Store</h1>

        <!-- Search Bar -->
        <div class="row justify-content-center w-100 mb-4">
            <div class="col-md-6 col-sm-8 col-10">
                <input type="text" class="form-control text-center search-input" placeholder="Search for products...">
            </div>
        </div>

        <!-- Electronics Categories -->
        <div class="container">
            <div class="row justify-content-center">
                <!-- Mobile Phones -->
                <div class="col-md-4 col-sm-6 mb-4">
                    <a href="mobiles.html" class="category-card">
                        <div class="category-img-container">
                            <img src="https://via.placeholder.com/150?text=Mobile" class="category-img" alt="Mobile Phones">
                        </div>
                        <div class="category-title">Mobile Phones</div>
                    </a>
                </div>

                <!-- Laptops -->
                <div class="col-md-4 col-sm-6 mb-4">
                    <a href="laptops.html" class="category-card">
                        <div class="category-img-container">
                            <img src="https://via.placeholder.com/150?text=Laptops" class="category-img" alt="Laptops">
                        </div>
                        <div class="category-title">Laptops</div>
                    </a>
                </div>

                <!-- Headphones -->
                <div class="col-md-4 col-sm-6 mb-4">
                    <a href="headphones.html" class="category-card">
                        <div class="category-img-container">
                            <img src="https://via.placeholder.com/150?text=Headphones" class="category-img" alt="Headphones">
                        </div>
                        <div class="category-title">Headphones</div>
                    </a>
                </div>

                <!-- Televisions -->
                <div class="col-md-4 col-sm-6 mb-4">
                    <a href="televisions.html" class="category-card">
                        <div class="category-img-container">
                            <img src="https://via.placeholder.com/150?text=TVs" class="category-img" alt="Televisions">
                        </div>
                        <div class="category-title">Televisions</div>
                    </a>
                </div>

                <!-- Smartwatches -->
                <div class="col-md-4 col-sm-6 mb-4">
                    <a href="smartwatches.html" class="category-card">
                        <div class="category-img-container">
                            <img src="https://via.placeholder.com/150?text=Smartwatches" class="category-img" alt="Smartwatches">
                        </div>
                        <div class="category-title">Smartwatches</div>
                    </a>
                </div>

                <!-- Cameras -->
                <div class="col-md-4 col-sm-6 mb-4">
                    <a href="cameras.html" class="category-card">
                        <div class="category-img-container">
                            <img src="https://via.placeholder.com/150?text=Cameras" class="category-img" alt="Cameras">
                        </div>
                        <div class="category-title">Cameras</div>
                    </a>
                </div>
            </div>
        </div>

    </div>

</body>
</html>
