import { Component } from '@angular/core';

@Component({
  selector: 'app-mobiles',
  templateUrl: './mobiles.component.html',
  styleUrls: ['./mobiles.component.css']
})
export class MobilesComponent {
  isMobileListVisible = false;

  mobiles = [
    {
      name: 'iPhone 14',
      price: '$999',
      features: 'A15 Bionic Chip, 6.1-inch OLED, 12MP Camera',
      image: 'assets/iphone14.jpg'
    },
    {
      name: 'Samsung Galaxy S23',
      price: '$899',
      features: 'Snapdragon 8 Gen 2, 6.5-inch AMOLED, 50MP Camera',
      image: 'assets/galaxy_s23.jpg'
    },
    {
      name: 'OnePlus 11',
      price: '$799',
      features: 'Snapdragon 8 Gen 2, 6.7-inch AMOLED, 48MP Camera',
      image: 'assets/oneplus11.jpg'
    },
    {
      name: 'Google Pixel 7',
      price: '$599',
      features: 'Tensor G2 Chip, 6.3-inch OLED, 50MP Camera',
      image: 'assets/pixel7.jpg'
    }
  ];

  showMobiles() {
    this.isMobileListVisible = !this.isMobileListVisible;
  }
}
